import React, {useState} from 'react'
import { newComp } from '../../Database'
import { auth } from '../../firebase'
import designIcon from '../../assets/socksUpload.png';
import * as ImagePicker from 'expo-image-picker';


export const CreateComp = ({route, navigation}) => {

  console.log(route)

    const [name, setName] = useState("")
    const [description, setDescription] = useState("")
    const [mode, setMode] = useState("")
    const [image, setImage] = useState("/")

    const addCompetition = async() =>{
        await handleImageUpload()
    }

    const pickImage = async () => {
        let result = await ImagePicker.launchImageLibraryAsync({
          mediaTypes: ImagePicker.MediaTypeOptions.All,
          allowsEditing: true,
          aspect: [4, 3],
          quality: 1,
        });

        console.log(result);

        if (!result.cancelled) {
          setImage(result.uri);
        }
      };


    const handleImageUpload = async() =>{
        const image = ref(storage, 'images/'+ title+'sokkies'+ Timestamp.fromDate(new Date()) + ".jpg" )


        const blob = await new Promise((resolve, reject) => {
            const xhr = new XMLHttpRequest();
            xhr.onload = function () {
              resolve(xhr.response);
            };
            xhr.onerror = function (e) {
              console.log(e);
              reject(new TypeError("Network request failed"));
            };
            xhr.responseType = "blob";
            xhr.open("GET", image, true);
            xhr.send(null);
        });




        await uploadBytes(image, blob).then((snapshot) => {
            getDownloadURL(snapshot.ref).then((url) => {
              console.log(url)

