import loginIcon from '../../assets/enter.png';

import React, {useState} from 'react';
import { StyleSheet, Text, View, Alert, TextInput, TouchableOpacity , Button, ActivityIndicator, Image, Dropdown, DropdownToggle, DropdownMenu} from 'react-native';
import { getAuth, createUserWithEmailAndPassword, signOut } from 'firebase/auth';
import { auth } from '../../firebase';
import { createUserOnRegister } from '../../Database';

export default function Register({navigation}) {

  const [username, onUsernameChange] = useState("");
  const [email, onEmailChange] = useState("");
  const [password, onPasswordChange] = useState("");
  const [adminrole, onAdminRoleChange] = useState("");
  const [studentrole, onStudentRoleChange] = useState("");
  const [loading, setLoading] = useState(false);

  const handleRegisterPress = () => {

      setLoading(true)

      createUserWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
          const user = userCredential.user;
          Alert.alert(user.uid);
          createUserOnRegister(user, username)
          setLoading(false)
      })
      .catch((error) => {
          Alert.alert(error.message);
          setLoading(false)
      });



  }




return (
  <View style={styles.container}>

  <Text style={styles.fontText1}>Sign Up</Text>

  <View style={styles.container}>

    <TouchableOpacity onPress={() => navigation.navigate("Profile")} style={styles.button}>
        <Text style={styles.card}>Let's Go!</Text>
    </TouchableOpacity>  
  </View>

  <TextInput
          style={styles.input}
          placeholder = "Your Username"
          value={username}
          onChangeText={onUsernameChange}
          />
      <TextInput
          style={styles.input}
          placeholder = "Your Email"
          value={email}
          onChangeText={onEmailChange}
          />
      <TextInput
          style={styles.input}
          placeholder = "Your Password"
          value={password}
          onChangeText={onPasswordChange}
          secureTextEntry={true}
    />

